/* various utils for the chat client */

/* The below class creates DOM for new message:

    messageData = {
        origin:  css class name: "message-self" | "message-other"
        name:    message author's name
        message: the message
        time:    timestamp (unix epoch)
    }

    <div class="message-other">
        <p class="sender-img">
            emojiFromName(senderName)
        </p>
        <p class="sender-name">
            senderName
        </p>
        <p class="message-content">
            message
        </p>
        <p class="timestamp">
            formatTimestamp(timestamp)
        </p>
    </div>
*/
class ChatMessage {
    constructor(senderName, message, time, type) {
        this.senderName = senderName;
        this.message = message;
        this.time = time;
        this.type = type;
        this._create();
    }

    _create() {
        const messageBox = document.createElement('div');
        messageBox.setAttribute('class', this.type);
        messageBox.append(this._newElement('p', 'sender-img', emojiFromName(this.senderName)));
        messageBox.append(this._newElement('p', 'sender-name', this.senderName));
        messageBox.append(this._newElement('p', 'message-content', this.message));
        messageBox.append(this._newElement('p', 'timestamp', this._formatTimestamp()));

        document.querySelector('#chat-log-container').prepend(messageBox);
    }

    _newElement(type, className, content) {
        const e = document.createElement(type);
        e.setAttribute('class', className);
        e.textContent = content;
        return e;
    }

    _formatTimestamp() {
        const date = new Date(this.time);
        var hours = date.getHours();
        var minutes = date.getMinutes();
        var ampm = hours >= 12 ? 'pm' : 'am';
        hours = hours % 12;
        hours = hours ? hours : 12; // the hour '0' should be '12'
        minutes = minutes < 10 ? '0'+minutes : minutes;
        var strTime = hours + ':' + minutes + ' ' + ampm;
        const months = ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];
        return `${date.getDate()} ${months[date.getMonth()]}, ${strTime}`;
    }
}

function generateHash(data) {
    var hash = 0;
    for (var i = 0; i < data.length; i++) {
        var char = data.charCodeAt(i);
        hash = ((hash<<5)-hash)+char;
        hash = hash & hash;
    }
    return Math.abs(hash);
}

